from sql_operate.models import EqxParsedfrom sql_operate.sql import dbclass ORMProxy(object):    """    ORM 业务代理    """    # ----------- EqxParsed ----------    @staticmethod    def save_eqx_parsed(data):        """        添加、更新数据        :param data:        :return:        """        data = EqxParsed(**data)        try:            db.session.merge(data)        finally:            db.session.commit()    @staticmethod    def delete_eqx_parsed_by_id(data_id):        """        删除数据        :param data_id:        :return:        """        try:            db.session.query(EqxParsed).filter(EqxParsed.id == data_id).delete()        finally:            db.session.commit()    @staticmethod    def find_eqx_parsed_by_paginate(page_index=1, data_count_per_page=1):        """        分页查找数据        :param page_index:        :param data_count_per_page:        :return:        """        return db.session.query(EqxParsed).paginate(page_index, data_count_per_page).items    @staticmethod    def find_eqx_parsed_by_id(data_id):        """        根据id找数据        :param data_id:        :return:        """        return db.session.query(EqxParsed).filter(EqxParsed.id == data_id).all()[0]    @staticmethod    def find_eqx_parsed_all():        """        查找所有数据        :return:        """        return db.session.query(EqxParsed).filter().all()